document.querySelector(".submit-btn").addEventListener("click",function(e){e.preventDefault();const siteName=document.getElementById("siteName").value.trim();const company=document.getElementById("company").value;const aiType=document.querySelector('input[name="aiType"]:checked').value;const description=document.getElementById("description").value.trim();const url=document.getElementById("url").value.trim();const icon=document.getElementById("icon").value.trim();const features=Array.from(document.querySelectorAll('input[name="features"]:checked')).map(checkbox=>checkbox.value);if(!siteName||!company||!aiType||!description||!url){alert("请填写必填字段");return}const siteData={id:Date.now().toString(),siteName:siteName,company:company,aiType:aiType,description:description,url:url,icon:icon,features:features,createdAt:(new Date).toISOString()};const sites=JSON.parse(localStorage.getItem("sites")||"[]");sites.push(siteData);localStorage.setItem("sites",JSON.stringify(sites));alert("网站信息添加成功！");document.getElementById("siteForm").reset();window.location.href="list.html"});document.querySelector(".cancel-btn").addEventListener("click",function(){if(confirm("确定要取消吗？所有未保存的更改将丢失。")){window.location.href="index.html"}});